import React, { Component } from 'react';
import Header from './CommonComponents/Header';
import Footer from './CommonComponents/Footer';
import Navbar from './CommonComponents/Navbar';
import AdminAuthDataService from './CommonComponents/AdminAuthDataService';

import axios from "axios";


class StudentAddAssignment extends Component {

    custom_file_upload_url = `YOUR_API_ENDPOINT_SHOULD_GOES_HERE`;

    constructor(props)
    {
        super(props)

        this.state = {
            id : null,name:"",description:"",start:"",end:"",email:"",
            dropdowncontent : [], 

        }

        this.handleSubmitFile = this.handleSubmitFile.bind(this);
    }


    handleChange(event)//This is a synthetic event
    {
        this.setState({[event.target.name]:event.target.value});
        console.log(this.state)
    }



    handleSubmitFile = () => {

        if (this.state.image_file !== null){

            let formData = new FormData();
            formData.append('customFile', this.state.image_file);
            // the image field name should be similar to your api endpoint field name
            // in my case here the field name is customFile

            axios.post(
                this.custom_file_upload_url,
                formData,
                {
                    headers: {
                        "Content-type": "multipart/form-data",
                    },                    
                }
            )
            .then(res => {
                console.log(`Success` + res.data);
            })
            .catch(err => {
                console.log(err);
            })
        }
    }


    render() {
        return (
            <div>
            <Header />
            <div className="container-fluid">
                <div className="row">
                    <div className="col-md-2 navbar-div">
                        <Navbar />
                    </div>
                    <div className="col-md-10 content-div">

                        <div className="content_holder">
                        <h2>Submit assignment</h2><hr/>
                            <div class="form-group">


                {/* image input field */}
                <input
                    type="file"
                    onChange={this.handleImagePreview}
                />
                <label>Upload file</label>
                            </div>


                            <button type="submit" class="btn btn-primary" onClick={this.handleSubmitFile}>Submit</button>
                        </div>

                    </div>
                </div>
            </div>
            <Footer />
        </div>
        );
    }
}

export default StudentAddAssignment;
