            phone_no: inputData.phoneNo,
            teacher_id: 0,
            user_name: "Teacher "+inputData.dob
          }
        let ret = axios.post(`http://localhost:8082/api/v1/admin/add-teacher`,data);
        return ret;
    }

    addClass(inputData)
    {
        let data={}
    }

}

export default new AdminAuthDataService();
